mapper.delete:

delete from song_list
where id = #{id,jdbcType=VARCHAR}
  and song_id = #{songId,jdbcType=VARCHAR}

mapper.update:

update song_list
set song_title = #{songTitle,jdbcType=VARCHAR},
  song_singer = #{songSinger,jdbcType=VARCHAR},
  song_url = #{songUrl,jdbcType=VARCHAR},
  song_comment_count = #{songCommentCount,jdbcType=VARCHAR},
  song_flag = #{songFlag,jdbcType=VARCHAR},
new_comment_count = #{newCommentCount,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}
  and song_id = #{songId,jdbcType=VARCHAR}

mapper.select:

select id, song_id, song_title, song_singer, song_url, song_comment_count, song_flag,new_comment_count
from song_list
where id = #{id,jdbcType=VARCHAR}
  and song_id = #{songId,jdbcType=VARCHAR}


select id, song_id, song_title, song_singer, song_url, song_comment_count, song_flag,new_comment_count
from song_list
where song_id = #{songId,jdbcType=VARCHAR}
